(function() {
	var Core = {
		initialized: false,
		initialize: function() {
			if (this.initialized)
				return;
			this.initialized = true;
			this.build();
		},
		build: function() {
			this.animations();
		},
		animations: function() {
			// Count To
			$(".number-counters [data-to]").each(function() {
				var $this = $(this);
				$this.appear(function() {
					$this.countTo({});
				}, {
					accX: 0,
					accY: -150
				});
			});
		},
	};
	Core.initialize();
})();


